import { getTodos } from "../lib/todoServices";

const initState = {
  todos: [],
  currentTodo: "",
};
export function loadTodos(todos) {
  return {
    type: "TODO_LOAD",
    payload: todos,
  };
}
export function addTodos(name) {
  return {
    type: "TODO_ADD",
    payload: name,
  };
}
export function fetchTodos() {
  return dispatch => {
    getTodos().then(todos => dispatch(loadTodos(todos)));
  };
}
export function saveTodo(name) {
  return dispatch => {
    createTodo(name).then(res => dispatch(addTodos(res)));
  };
}
export function updateCurrent(val) {
  return {
    type: "UPDATE_CURRENT",
    payload: val,
  };
}
export default (state = initState, action) => {
  switch (action.type) {
    case "TODO_ADD":
      return { ...state, todos: state.todos.concat(action.payload) };
    case "TODO_LOAD":
      return { ...state, todos: action.payload };
    case "UPDATE_CURRENT": {
      return { ...state, currentTodo: action.payload };
    }
    default:
      return state;
  }
};
