import React from 'react'
import {connect} from 'react-redux'
import {updateCurrent} from '../reducers/todo' 
import store from '../store'
const TodoForm = ({currentTodo, updateCurrent}) => {
  const handleInputChange = (evt) => {
    const val = evt.target.value
    updateCurrent(val)
  }
  return (
    <form className="form-inline" ><input className="form-control" type="text" 
        onChange={handleInputChange}
        value={currentTodo}
      />
    </form>
  )
} 

// const mapStateToProps = (state) => ({currentTodo: state.currentTodo})
// const mapDispatchToProps= ({updateCurrent})
export default connect(
  (state) => ({currentTodo: state.currentTodo}),
  {updateCurrent}
)(TodoForm)
